import React from "react";
import styled from "styled-components";

const DetailWrap = ({ children }) => {
  return <Wrap>{children}</Wrap>;
};

const Wrap = styled.div`
  max-width: 70rem;
  width: 100%;
  margin: 0 auto;
  padding: 1rem;
`;
export default DetailWrap;
