import React from "react";
import styled from "styled-components";
import palette from "../../../lib/styles/palette";
const Experience = ({ item }) => {
  return (
    <ExperienceItem>
      <img
        src={`../images/company/${item.thumbnail}.png`}
        alt="experience logo"
      />
      <MetaBox>
        <MataTitle>{item.title}</MataTitle>
        <MetaDesc>{item.desc}</MetaDesc>
        <div>{item.date}</div>
      </MetaBox>
    </ExperienceItem>
  );
};

const ExperienceItem = styled.div`
  display: flex;
  align-items: center;
  & > img {
    width: 5rem;
    margin-right1rem
  }
  & + & {
    margin-top: 1rem;
  }
`;

const MetaBox = styled.div`
  display: flex;
  flex-direction: column;
  color: ${palette.white};
`;
const MataTitle = styled.p`
  font-size: 1.2rem;
  font-weight: 500;
  margin: 0;
`;
const MetaDesc = styled.p`
  font-size: 1rem;
  font-weight: 400;
  margin: 0;
`;

const certifications = [
  {
    id: 1,
    title: "정보처리기사",
    thumbnail: "hrd_logo",
    date: "2019.05",
  },
  {
    id: 2,
    title: "리눅스마스터 2급",
    thumbnail: "kait_logo",
    date: "2018.12",
  },
  {
    id: 3,
    title: "네트워크관리사 2급",
    thumbnail: "icpa_logo",
    date: "2018.12",
  },
  {
    id: 4,
    title: "정보처리산업기사",
    thumbnail: "hrd_logo",
    date: "2018.11",
  },
];

export default Experience;
