import React from "react";
import styled from "styled-components";
import palette from "../lib/styles/palette";

const Main = () => {
  return (
    <MainBolok>
      <Bg></Bg>
      <GradientBox>
        <MainWrap>
          <TextBox>
            <Desc>Front-end developer</Desc>
            <Title>윤혜빈 입니다.</Title>
            <Desc>소개소개</Desc>
          </TextBox>
        </MainWrap>
      </GradientBox>
    </MainBolok>
  );
};

const MainBolok = styled.section`
  width: 100%;
  height: 35rem;
  position: relative;
`;

const Bg = styled.div`
  position: absolute;
  background: url("../images/main-bg2.jpg");
  background-size: cover;
  width: 100%;
  height: 100%;
  background-position: center;
`;

const MainWrap = styled.div`
  width: 100%;
  max-width: 70rem;
  margin: 0 auto;
  height: 100%;
  padding: 1rem;
`;

const GradientBox = styled.div`
  width: 100%;
  display: flex;
  position: absolute;
  display: flex;
  flex-direction: column;
  position: absolute;
  bottom: 0;
  height: 12rem;
  background: linear-gradient(
    360deg,
    rgba(0, 0, 0, 1) 8.98%,
    rgba(0, 0, 0, 0) 100%
  );
`;

const TextBox = styled.div``;

const Title = styled.h1`
  color: ${palette.text_white};
  font-size: 1.5rem;
  font-weight: 600;
  margin: 0.5rem 0;
`;

const Desc = styled.p`
  color: ${palette.text_white};
  font-size: 0.9rem;
  font-weight: 500;
  margin: 0;
`;

export default Main;
