import React, { useEffect, useRef, useState } from "react";
import styled, { css } from "styled-components";
import palette from "../../lib/styles/palette";
import { RiArrowLeftSLine, RiArrowRightSLine } from "react-icons/ri";
const List = ({ vertical, content, component: Component }) => {
  const scrollRef = useRef(null);
  const [hasScroll, setHasScroll] = useState();
  const [leftLocation, setlleftLocation] = useState(false);

  useEffect(() => {
    resizeWindow();
    window.addEventListener("resize", resizeWindow);
    return () => window.removeEventListener("resize", resizeWindow);
  }, [hasScroll]);

  const onScroll = () => {
    console.log(scrollRef);
  };
  const resizeWindow = () => {
    const hasHorizontalScrollbar =
      scrollRef.current.scrollWidth > scrollRef.current.clientWidth;
    setHasScroll(hasHorizontalScrollbar);
  };

  const onClick = (e) => {
    console.log();
    // scrollRef.current.scrollIntoView({ behavior: "smooth" });
    const dd = leftLocation ? 0 : scrollRef.current.scrollHeight;
    console.log(dd);
    scrollRef.current.scrollTo({
      top: window.scrollTop,
      left: dd,
      behavior: "smooth",
    });
    // scrollRef.current.scrollLeft = leftLocation
    //   ? 0
    //   : scrollRef.current.scrollHeight;
    setlleftLocation(!leftLocation);
  };

  return (
    <SkillListBlock ref={scrollRef} vertical={vertical} onScroll={onScroll}>
      {hasScroll && (
        <MoveButton left={leftLocation} onClick={onClick}>
          {leftLocation ? <RiArrowLeftSLine /> : <RiArrowRightSLine />}
        </MoveButton>
      )}
      {content &&
        content.map((item) => (
          <Component key={item.id} item={item}></Component>
        ))}
    </SkillListBlock>
  );
};

const MoveButton = styled.button`
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0.5rem;
  border-radius: 50%;
  background: ${palette.white};
  cursor: pointer;
  color: ${palette.gray};
  align-self: center;
  outline: 0;
  border: 0;
  position: absolute;
  font-size: 1.5rem;
  right: -1rem;
  ${(props) =>
    props.left &&
    css`
      left: -1rem;
    `}
`;

const SkillListBlock = styled.div`
  display: flex;
  ${(props) =>
    props.vertical &&
    css`
      flex-direction: column;
    `}

  overflow-x: auto;
  padding-bottom: 1rem;
  &::-webkit-scrollbar {
    visibility: hidden;
  }
`;

export default List;
