import React, { useEffect, useRef, useState } from "react";
import styled, { css } from "styled-components";
const List = ({ vertical, content, component: Component }) => {
  const scrollRef = useRef(null);
  const [hasScroll, setHasScroll] = useState();

  let resizeWindow = () => {
    const hasHorizontalScrollbar =
      scrollRef.current.scrollWidth > scrollRef.current.clientWidth;
    console.log(hasHorizontalScrollbar);
    setHasScroll(hasHorizontalScrollbar);
  };

  useEffect(() => {
    resizeWindow();
    window.addEventListener("resize", resizeWindow);
    return () => window.removeEventListener("resize", resizeWindow);
  }, []);

  const onClick = (e) => {
    // console.log(scrollRef.current.clientLeft);
    // console.log(scrollRef.current.scrollLeft);
    console.log(scrollRef);

    const hasHorizontalScrollbar =
      scrollRef.current.scrollWidth > scrollRef.current.clientWidth;
    console.log(hasHorizontalScrollbar);
  };

  return (
    <SkillListBlock ref={scrollRef} vertical={vertical}>
      {hasScroll && <button onClick={onClick}>dddd</button>}
      {content &&
        content.map((item) => (
          <Component key={item.id} item={item}></Component>
        ))}
    </SkillListBlock>
  );
};

const SkillListBlock = styled.div`
  display: flex;
  ${(props) =>
    props.vertical &&
    css`
      flex-direction: column;
    `}

  overflow-x: auto;
  padding-bottom: 1rem;
  &::-webkit-scrollbar {
    /* visibility: hidden; */
    width: 4px;
    height: 4px;
    border-radius: 6px;
    background: rgba(255, 255, 255, 0.1);
  }
  &::-webkit-scrollbar-thumb {
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 6px;
  }
`;

export default List;
